import { PublicKey as SolanaPublicKey } from '@solana/web3.js';
import { PublicKey as NearPublicKey } from 'near-api-js/lib/utils';
export interface SolanaOnConnect {
    publicKey: SolanaPublicKey;
}
export interface AppMetadata {
    application: string;
    description: string;
    additionalInfo: string;
    icon: string;
}
export interface SolanaAppInfo {
    appMetadata: AppMetadata;
    onUserConnect: (params: SolanaOnConnect) => void;
    url?: string;
    timeout?: number;
}
export interface NearOnConnect {
    publicKey: NearPublicKey;
    accountId: string;
}
export interface NearAppInfo {
    appMetadata: AppMetadata;
    onUserConnect: (params: NearOnConnect) => void;
    url?: string;
    timeout?: number;
}
